/* 
 * Exercise 2.2 (Computing the volume of a cylinder) Write a program that reads in the radius and
length of a cylinder and computes volume using the following formulas:
area = radius * radius * Ï€
volume = area * length
 */

import java.util.Scanner;


public class Exercise02_02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// make scanner object
		Scanner getdouble = new Scanner(System.in);
		// prompt input
		System.out.print("Enter the radius and length of a cylinder: ");
		// get radius
		double getradius = getdouble.nextDouble();
		double getlength = getdouble.nextDouble();
		// calculate area: area = radius * radius * PI
		double area = getradius * getradius * Math.PI;
		// calculate volume: volume = area * length
		double volume = area * getlength;
		// output
		System.out.printf("The area is %.4f%n", area);
		System.out.printf("The volume is %.1f ", volume);
		
	}

}
