
/*
 * Exercise 4.1 (Geometry: area of pentagon) Write a program that prompts the user to enter 
 * the length from the center of a pentagon to a vertex and computes the area of the pentagon,
 * as shown in the diagram.
 * 
 */
import java.util.Scanner;

public class Exercise04_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Get the length
		 Scanner input = new Scanner(System.in);
		 
		 double r, s, area;
		  
		 System.out.print("Enter the length from the center to a vertex:");
		 r = input.nextDouble();
		  
		 s = 2 * r * Math.sin(Math.PI/5);
		 		 
		 area = (5 * Math.pow(s,2)) / (4 * Math.tan(Math.PI/5));
		
		 System.out.printf("The area of the pentagon is %.2f", area);
		
	}

}
